import { Component, OnInit, ViewChild } from '@angular/core';
import {
  FormBuilder,
  FormControl,
  FormGroup,
  Validators,
} from '@angular/forms';
import { Employee } from './model/employee.model';
import { EmployeeService } from './services/employee.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent implements OnInit {
  @ViewChild('fileInput') fileInput: any;
  @ViewChild('addEmployeeButton') addEmployeeButton: any;
  public sidebarShow: boolean = false;
  employees: Employee[];
  employeesToDisplay: Employee[];
  employeeForm: FormGroup;
  actionButton: string;
  editData: Boolean = false;
  empId: number | undefined;
  outdatedEmployeeDetail: Employee;
  imageUrl = '';

  constructor(
    private fb: FormBuilder,
    private employeeService: EmployeeService
  ) {
    this.actionButton = 'Insert';
    this.employeeForm = fb.group({});
    this.employees = [];
    this.employeesToDisplay = [];
    this.outdatedEmployeeDetail = {
      firstname: '',
      lastname: '',
      birthdate: '',
      gender: '',
      education: '',
      profile: '',
    };
  }

  educationOptions = [
    '10th pass',
    'diploma',
    'graduate',
    'post graduate',
    'PhD',
  ];

  ngOnInit(): void {
    this.actionButton = 'Insert';
    this.editData = false;
    this.employeeForm = this.fb.group({
      firstname: ['', Validators.required],
      lastname: ['', Validators.required],
      birthday: ['', Validators.required],
      gender: ['m', Validators.required],
      education: ['default', Validators.required],
      profile: ['', Validators.required],
    });

    this.employeeService.getEmployees().subscribe((res) => {
      for (let emp of res) {
        this.employees.unshift(emp);
      }
      this.employeesToDisplay = this.employees;
    });
  }

  closeModal() {
    this.clearForm();
  }

  onselectFile(e: any) {
    if (e.target.files) {
      var reader = new FileReader();
      reader.readAsDataURL(e.target.files[0]);
      reader.onload = (event: any) => {
        this.imageUrl = event.target.result;
      };
    }
  }

  updateProduct() {
    let updatedEmployeeDetail: Employee = {
      firstname: this.FirstName.value,
      lastname: this.LastName.value,
      birthdate: this.BirthDay.value,
      gender: this.Gender.value,
      education: this.educationOptions[parseInt(this.Education.value)],
      profile: this.imageUrl,
      id: this.outdatedEmployeeDetail.id,
    };
    if (this.employeeForm.valid) {
      this.employeeService
        .putProduct(
          updatedEmployeeDetail,
          this.outdatedEmployeeDetail.id?.toString() ?? '0'
        )
        .subscribe((res) => {
          let index = this.employees.indexOf(this.outdatedEmployeeDetail);
          this.employees[index] = res as Employee;
        });
    }
    this.clearForm();
  }

  addEmployee() {
    if (!this.editData) {
      if (this.employeeForm.valid) {
        let employee: Employee = {
          firstname: this.FirstName.value,
          lastname: this.LastName.value,
          birthdate: this.BirthDay.value,
          gender: this.Gender.value,
          education: this.educationOptions[parseInt(this.Education.value)],
          profile: this.imageUrl,
        };
        this.employeeService.postEmployee(employee).subscribe((res) => {
          this.employees.unshift(res);
          this.clearForm();
        });
      }
      this.sidebarShow = false;
    } else {
      this.updateProduct();
    }
  }

  removeEmployee(event: any) {
    this.employees.forEach((val, index) => {
      if (val.id === parseInt(event)) {
        this.employeeService.deleteEmployee(event).subscribe((res) => {
          this.employees.splice(index, 1);
        });
      }
    });
  }

  setForm(emp: Employee) {
    this.actionButton = 'Edit';
    this.FirstName.setValue(emp.firstname);
    this.LastName.setValue(emp.lastname);
    this.BirthDay.setValue(emp.birthdate);
    this.Gender.setValue(emp.gender);

    let educationIndex = 0;
    this.educationOptions.forEach((val, index) => {
      if (val === emp.education) educationIndex = index;
    });
    this.Education.setValue(educationIndex);
    this.fileInput.nativeElement.value = '';
  }

  editEmployee(event: any) {
    this.employees.forEach((val, index) => {
      if (val.id === parseInt(event)) {
        this.setForm(val);
        this.outdatedEmployeeDetail = val;
      }
    });
    this.addEmployeeButton.nativeElement.click();
    this.editData = true;
  }

  clearForm() {
    this.actionButton = 'Insert';
    this.FirstName.setValue('');
    this.LastName.setValue('');
    this.BirthDay.setValue('');
    this.Gender.setValue('');
    this.Education.setValue('');
    this.fileInput.nativeElement.value = '';
  }

  searchEmployees(event: any) {
    let filteredEmployees: Employee[] = [];
    if (event === '') {
      this.employeesToDisplay = this.employees;
    } else {
      filteredEmployees = this.employees.filter((val, index) => {
        let targetKey =
          val.firstname.toLowerCase() + '' + val.lastname.toLocaleLowerCase();
        let searchKey = event.toLowerCase();
        return targetKey.includes(searchKey);
      });
      this.employeesToDisplay = filteredEmployees;
    }
  }

  public get FirstName(): FormControl {
    return this.employeeForm.get('firstname') as FormControl;
  }
  public get LastName(): FormControl {
    return this.employeeForm.get('lastname') as FormControl;
  }
  public get BirthDay(): FormControl {
    return this.employeeForm.get('birthday') as FormControl;
  }
  public get Gender(): FormControl {
    return this.employeeForm.get('gender') as FormControl;
  }
  public get Education(): FormControl {
    return this.employeeForm.get('education') as FormControl;
  }
}
<style></style>

<div class="toolbar">
  <div class="sidebar-opener">
    <i
      class="fa fa-bars fa-lg sidebar-opener__icon"
      (click)="sidebarShow = !sidebarShow"
    ></i>
    <h2 class="sidebar-opener__text">Welcome !</h2>
  </div>
  <div class="search-employee">
    <input
      type="search"
      class="search-employee__input"
      placeholder="Search Employee"
      (input) = "searchEmployees(searchInput.value)"
      #searchInput
    />
  </div>
  <div class="sidebar-slider" [class.sidebar-slide-in]="sidebarShow" #sideBar>
    <div class="sidebar-close" (click)="sidebarShow = !sidebarShow">X</div>
    <div class="sidebar-content">
      <ul>
        <li
          type="button"
          class="add-employee__button"
          data-toggle="modal"
          data-target="#exampleModal"
          #addEmployeeButton
        >
          Add Employee
        </li>
        <li
          type="button"
          class="download-details__button"
        >
          Download Records (PDF Format)
        </li>
        <li
          type="button"
          class="download-details__button"
        >
          Download Records (Excel Format)
        </li>
      </ul>
    </div>
  </div>
</div>

<!-- Employee Details -->
<main class="main">
  <div *ngIf="employees.length === 0">There are No Employees Added!</div>
  <div *ngFor="let emp of employeesToDisplay">
    <app-employee
      [employee]="emp"
      (onRemoveEmployee)="removeEmployee($event)"
      (onEditEmployee)="editEmployee($event)"
    ></app-employee>
  </div>
</main>

<!-- Modal To Enter the Employee Information -->
<div
  class="modal fade"
  id="exampleModal"
  tabindex="-1"
  role="dialog"
  aria-labelledby="exampleModalLabel"
  aria-hidden="true"
  data-backdrop="static"
>
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">
          Enter Employee Information
        </h5>
        <button
          type="button"
          class="close"
          data-dismiss="modal"
          aria-label="Close"
          (click)="closeModal()"
        >
          X
        </button>
      </div>
      <div class="modal-body">
        <form [formGroup]="employeeForm">
          <div class="modal-body__personal-info">
            <h6 class="modal-body__personal-info__text">
              Personal Information
            </h6>
          </div>
          <div class="modal-body__name">
            <div class="modal-body__name__first-name">
              <input
                type="text"
                class="form-control"
                placeholder="First Name"
                formControlName="firstname"
              />
            </div>
            <div class="modal-body__name__last-name">
              <input
                type="text"
                class="form-control"
                placeholder="Last Name"
                formControlName="lastname"
              />
            </div>
          </div>
          <div class="modal-body__personal-info__details">
            <div class="modal-body__personal-info__details__datepicker">
              <label for="datepicker" class="form-label">Birthdate</label>
              <input
                type="date"
                class="form-control"
                id="datepicker"
                formControlName="birthday"
              />
            </div>
            <div class="modal-body__personal-info__details__gender">
              <h6>Gender</h6>

              <div class="modal-body__personal-info__details__gender__checkbox">
                <div class="form-check form-check-inline">
                  <input
                    class="form-check-input"
                    type="radio"
                    name="gender"
                    id="male"
                    value="m"
                    formControlName="gender"
                  />
                  <label class="form-check-label" for="male">Male</label>
                </div>
                <div class="form-check form-check-inline">
                  <input
                    class="form-check-input"
                    type="radio"
                    name="gender"
                    id="female"
                    value="f"
                    formControlName="gender"
                  />
                  <label class="form-check-label" for="female">Female</label>
                </div>
              </div>
            </div>
          </div>
          <div class="modal-body__eduction">
            <label for="education" class="form-label">Education</label>
            <select
              class="form-control modal-body__eduction__select"
              formControlName="education"
            >
              <option value="default" selected>Enter your Education</option>
              <option
                *ngFor="let option of educationOptions; let i = index"
                value="{{ i }}"
              >
                {{ option | titlecase }}
              </option>
            </select>
          </div>
          <div class="modal-body__profile-image">
            <label for="profile" class="form-label">Profile Image</label>
            <input
              type="file"
              class="form-control modal-body__profile-image__input"
              id="profile"
              #fileInput
              formControlName="profile"
              (change)="onselectFile($event)"
            />
          </div>
        </form>
      </div>
      <div class="modal-footer">
        <button
          type="button"
          class="btn btn-primary"
          data-dismiss="modal"
          (click)="addEmployee()"
        >
          {{ actionButton }}
        </button>
      </div>
    </div>
  </div>
</div>
